document.addEventListener("DOMContentLoaded", evt => {
 let inputFilter = document.querySelector('#inputFilter');
 let pTags = [...document.querySelectorAll('#el_strings p')];

 inputFilter.addEventListener('input', () => {
  pTags.forEach(el => {
   console.log(el.innerHTML);
   if (el.innerHTML.includes(inputFilter.value)) {
    el.classList.remove('hidden');
   } else {
    el.classList.add('hidden');
   }
  })
 })
});

document.addEventListener("DOMContentLoaded", evt => {
 let inputFilter = document.querySelector('#inputFilter');
 let elStrings = document.querySelector('#el_strings');
 let pTags = [...document.querySelectorAll('#el_strings p')];

 inputFilter.addEventListener('input', () => {
  elStrings.innerHTML = '';
  pTags.filter(el => el.innerHTML.includes(inputFilter.value))
   .forEach(el => elStrings.append(el));
  })
});

document.addEventListener("DOMContentLoaded", evt => {
 let btnSort = document.querySelector('#btnSort');
 let elNumbers = document.querySelector('#el_numbers');
 let pTags = [...document.querySelectorAll('#el_numbers p')];

 btnSort.addEventListener('click', () => {
  pTags.sort((a, b) => Number(a.innerHTML) > Number(b.innerHTML) ? 1 : -1)
  elNumbers.innerHTML = '';
  pTags.forEach(el => elNumbers.append(el));
 })
});

