document.addEventListener("DOMContentLoaded", evt => {

    // Variablen
    let eingabeElemente = document.querySelectorAll('.eingabe');
    let elementdaten = new Map();

    // FUNKTIONEN
    const init = () => {
        eingabeElemente.forEach(el => {
            let ta = el.querySelector('textarea');
            let btn = el.querySelector('button');

            // Undo-Stack (und ggf. anderes) anlegen
            elementdaten.set(el, {
                undo: ''
            })

            // Undo-Schritt eintragen
            ta.addEventListener('keydown', evt => {
                if (evt.key != 'Shift' &&
                    evt.key != 'Control' &&
                    evt.key != 'Alt'
                ) {
                    elementdaten.get(el).undo = ta.value;
                };

            })

            // Undo-Button
            btn.addEventListener('click', evt => {
                ta.value = elementdaten.get(el).undo;
            })


        })
        console.log(elementdaten);

    }

    // INIT
    init();

});

document.addEventListener("DOMContentLoaded", evt => {

    // Variablen
    let eingabeElemente = document.querySelectorAll('.eingabe');
    let eingaben = document.querySelector('#eingaben');
    let elementdaten = new Map();
    let ignoreKeys = 'Shift Control Alt ArrowLeft ArrowRight ArrowUp ArrowDown'.split(' ');


    let texte = [
        'Er hörte leise Schritte hinter sich. Das bedeutete nichts Gutes.',
        'Wer würde ihm schon folgen, spät in der Nacht und dazu noch in dieser engen Gasse mitten im übel beleumundeten Hafenviertel?',
        'Gerade jetzt, wo er das Ding seines Lebens gedreht hatte und mit der Beute verschwinden wollte!',
        'Hatte einer seiner zahllosen Kollegen dieselbe Idee gehabt, ihn beobachtet und abgewartet, um ihn nun um die Früchte seiner Arbeit zu erleichtern?'
    ]

    // FUNKTIONEN
    // Debouncing wird erst durchgeführt, wenn nach einer Wartezwit (delay)
    // Die Funktion kein weiteres Mal angestoßen wird
    const debounce = (dieFunktion, delay) => {
        let warte;
        return evt => {
            let puffer = evt.target.value;
            clearTimeout(warte);
            warte = setTimeout(
                () => dieFunktion(evt, puffer),
                delay
            )
        }
    }


    const DOMElementAnlegen = ({
        inhalt = '',
        typ = 'div',
        eltern = ausgabe,
        klassen = [],
        attr = {},
        listeners = {},
        styles = {},
        amEnde = true,
    } = {}) => {
        let neu = document.createElement(typ);
        if (inhalt) neu.innerHTML = inhalt;
        if (klassen.length) neu.className = klassen.join(' ');

        Object.entries(attr).forEach(el => neu.setAttribute(...el));
        Object.entries(listeners).forEach(el => neu.addEventListener(...el));
        Object.entries(styles).forEach(style => neu.style[style[0]] = style[1]);

        console.log(eltern);


        if (!amEnde && eltern.children.length) eltern.insertBefore(neu, eltern.children[0]);
        else eltern.append(neu);

        return neu;
    }

    const templateEingabeelement = (inhalt = '', eltern = eingaben) => {
        let container = DOMElementAnlegen({
            eltern,
            klassen: ['eingabe']
        })

        // Undo-Stack (und ggf. anderes) anlegen
        elementdaten.set(container, {
            undostack: []
        })

        // Textarea
        let ta = DOMElementAnlegen({
            typ: 'textarea',
            eltern: container,
            inhalt,
            listeners: {
                // Undo-Schritt eintragen
                keydown: debounce(tastenanschlag, 200)
            }
        })

        // Button
        DOMElementAnlegen({
            typ: 'button',
            eltern: container,
            inhalt: 'Undo',
            listeners: {
                click(evt) {
                    let undo = elementdaten.get(container).undostack;
                    console.log(undo);

                    if (undo.length) {
                        ta.value = undo.splice(undo.length - 1, 1)[0];
                    }
                }
            }
        })
    }

    const tastenanschlag = (evt, puffer) => {
        if (!ignoreKeys.includes(evt.key)) {
            elementdaten.get(evt.target.parentNode).undostack.push(puffer);
        };
    }

    const init = () => {
        texte.forEach(text => {
            templateEingabeelement(text)
        })
    }

    // INIT
    init();

});

