        let people = [...data.people].filter(([key, val]) => {
            return (
                val.nName.toLowerCase().includes(inpFilter.value.toLowerCase())
                || val.nName.toLowerCase().includes(inpFilter.value.toLowerCase())
            )
        });
        people = new Map(people);

