let selected = false;

const handleClick = evt => {
 let el = evt.target;
 if (selected) {
  // el ist das aktuell angeklickte Element
  // selected ist das zuvor angeklickte Element
  let temp = selected.innerHTML;
  selected.innerHTML = el.innerHTML;
  el.innerHTML = temp;

  selected.classList.remove('highlight');
  selected = false;
 } else {
  el.classList.add('highlight');
  selected = el;
 }
}

