const elements = {};

const domMapping = () => {
 elements.btnAdd = document.querySelector('button#btnAdd');
 elements.btnSend = document.querySelector('button#btnSend');
 elements.containerInputs = document.querySelector('#containerInputs');
 elements.inputs = Array.from(document.querySelectorAll('input[type="text"]'));
 elements.form = document.querySelector('form');
}

const applyEventListeners = () => {
 elements.btnAdd.addEventListener('click', addInput);
 elements.form.addEventListener('submit', handleSubmit); 
}

const addInput = () => {
 const pTag = document.createElement('p');
 pTag.innerHTML = `Eingabe ${elements.inputs.length + 1}:`
 elements.containerInputs.append(pTag);

 const input = document.createElement('input');
 input.type = 'text';
 input.name = `input${elements.inputs.length + 1}`;
 input.placeholder = `Textinhalt`;
 pTag.append(input);

 domMapping();
}

const handleSubmit = evt => {
 evt.preventDefault();
}

const handleClick = () => {
 for (let input of elements.inputs) {
  console.log(input.value);
 }
}

