const isPrime = (zahl) => {
 if (zahl < 2) return false; // Zahlen kleiner als 2 sind keine Primzahlen
 for (let i = 2; i <= Math.sqrt(zahl); i++)
  if (zahl % i === 0) return { zahl, isPrime: false, divider: i }; // Teilbarkeit gefunden

 return { zahl, isPrime: true }; // Zahl ist eine Primzahl
};

self.addEventListener('message', ({ data }) => {
 let result = isPrime(data);
 self.postMessage(result);
})

