 if (zahl < 2) return false;
  for (let i = 2; i < zahl; i++) {
   if (i % 1e6 == 0) {
    self.postMessage({
     status: 'progress',
     zahl,
     progress: i / zahl
    })
   }
   if (zahl % i === 0) {
    return {
     status: 'done',
     zahl,
     isPrime: false,
     divider: i
    }
   };
  }
  return {
   status: 'done',
   zahl,
   isPrime: true
  }; // Zahl ist eine Primzahl
};

self.addEventListener('message', ({ data }) => {
 let result = isPrime(data);
 self.postMessage(result);
})

