// Worker
const handleSquare = data => {
 return {
  type: 'square',
  circumference: 4 * data.width,
  area: data.width ** 2,
  width: data.width
 }
}

const handleCircle = data => {
 return {
  type: 'circle',
  circumference: 2 * Math.PI * data.radius,
  area: Math.PI * data.radius ** 2,
  radius: data.radius
 }
}

const handleEllipse = data => {
 return {
  type: 'ellipse',
  circumference: 2 * Math.PI * Math.sqrt(
   (data.majorAxis ** 2) + (data.minorAxis ** 2)
  ),
  area: Math.PI * data.majorAxis * data.minorAxis,
  majorAxis: data.majorAxis,
  minorAxis: data.minorAxis
 }
}


self.addEventListener('message', ({ data }) => {
 switch (data.type) {
  case 'square':
   self.postMessage(handleSquare(data))
  break;
  case 'circle':
   self.postMessage(handleCircle(data))
  break;
  case 'ellipse':
   self.postMessage(handleEllipse(data))
  break;
  default:
  break;
 }
})

// Worker
let storage = [];

const storeData = payload => {
 payload.forEach(val => storage.push(val));
}

const sortAndGet = sortBy => {
 return storage
  .filter(val => val[sortBy])
  .toSorted((a, b) => (a[sortBy] > b[sortBy]) ? 1 : -1);
}

self.addEventListener('message', ({ data }) => {
 if (data.task == 'send') storeData(data.payload);

 if (data.task == 'sortAndGet') {
  self.postMessage({
   task: 'sortAndGet',
   payload: sortAndGet(data.sortBy)
  });
 }
})


