            let anzahl = 5;
            let kreisradius = 8;

            // FUNKTIONEN
            const zufallErzeugen = (min, max, dez = 0) => {
                dez = 10 ** dez;
                let z = Math.random() * (max - min + (1 / dez)) + min;
                z = ~~(z * dez) / dez;
                return z;
            }

            const farbeErzeugen = () => `hsl(${zufallErzeugen(0, 360)},100%,50%)`;

            // KLASSEN
            const zeichne = data => {
                ctx.clearRect(0, 0, c.width, c.height);

                let gesamt = data.reduce((sum, wert) => sum + wert);
                let radius = 350;
                let winkel = 0;
                let center = [c.width / 2, c.height / 2];

                data.forEach((wert, i) => {
                    ctx.fillStyle = farbeErzeugen();
                    let anteil =  (360 / gesamt * wert) * (2 * Math.PI / 180);
                    ctx.beginPath();
                    ctx.moveTo(...center);
                    ctx.arc(...center, radius, winkel, winkel + anteil);
                    ctx.closePath();
                    ctx.fill();
                    ctx.stroke();
                    winkel += anteil;
                })

            }


            const init = () => {
                for (let i = 0; i < anzahl; i++) {
                    data.push(zufallErzeugen(10, 5000))
                }
                zeichne(data);
            }

            init();

        });

