const checkIfPrime = num => {
 return new Promise((resolve, reject) => {
  // Sonderfälle 0 und 1
  if (num <= 1) reject({
   status: 'done',
   prime: false,
   num,
   divider: false
  })

  for (let i = 2; (i ** 2) <= num; i++) {
   if (!(num % i)) reject({
    status: 'done',
    prime: false,
    divider: i,
    num
   })
  }

  resolve({
   status: 'done',
   prime: true,
   num,
  })
 })

}

checkIfPrime(29).then(
 console.log            
).catch(
 console.warn            
)

const checkIfPrime = num => {
 return new Promise((resolve, reject) => {
  if (num <= 1) reject({
   status: 'done',
   prime: false,
   num
  })
  for (let i = 2; (i ** 2) <= num; i++) {
   if (!(num % i)) reject({
    status: 'done',
    prime: false,
    divider: i,
    num
   })
  }
  resolve({
   status: 'done',
   prime: true,
   num,
  })
 })
}

const searchPrime = (num = 1, startWith = 0) => {
 return new Promise(async (resolve, reject) => {
  const arr = []
  while (arr.length < num) {
   try {
    let res = await checkIfPrime(startWith++);
    arr.push(res.num)
   } catch (err) {
    // console.log(err.num);                        
   }
  }
  resolve(arr)
 })
}

